async function search(query) {
  const name = query.toLowerCase().replace(/[^a-z0-9 ]/g, '').replace(/\s+/g, '-');
  const url = `https://www.bombuj.si/online-movie-${name}`;

  const res = await fetch(url, { method: 'HEAD' });
  if (!res.ok) return [];

  return [{
    title: query,
    url: url,
    thumbnail: null
  }];
}
